import main from "@/main"
import statusalert from '@/js/enum/statusalert';
import { createApp, h ,nextTick } from 'vue'
import MNotificationVue from '@/components/base/MNotification.vue';
const createToast = (message, type) => {
    const app = createApp({
      render() {
        return h(MNotificationVue, {
          message,
          type
        })
      }
    })
  
    const instance = app.mount(document.createElement('div'))
    document.body.appendChild(instance.$el)
  
    nextTick(() => {
        instance.$el.classList.add('show')
        setTimeout(() => {
          instance.$el.classList.remove('show')
          setTimeout(() => {
            app.unmount(instance.$el)
          }, 300)
        }, 3000)
      })
    
  }
  
  export default createToast
