import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    toast: {
      isShow: false,
      message: '',
      type: ''
    }
  },
  mutations: {
    setToast(state, payload) {
      state.toast = payload
    }
  },
  actions: {
    showInform({ commit }, payload) {
      commit('setToast', payload)
      setTimeout(() => {
        commit('setToast', {
          isShow: false,
          message: '',
          type: ''
        })
      }, 4000)
    }
  }
})
