const format={
         /**
      * Format lại về dạng tiền
      * Author:PXTRUONG(24/05/2023)
      */
         fomatmoney(money){
            try { 
                    money = parseFloat(money).toFixed(0);
                    money = money.toString();
                    return money.replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1.");  
            } catch (error) {
                return error
            }
        },
};
export default format
