import axios from "axios";
const request=axios.create({
        baseURL: process.env.VUE_APP_API_URL,
}) 

export const get =async(path,Options)=>{
    const res= await request.get(path,Options);
    return res.data;
}
export const deleteRecord = async(path,Options)=>{
    const res= await request.delete(path,Options);
    return res.data;
} 
export const add  =async(path,Options)=>{
    const res= await request.post(path,Options);
    return res.data;
}
export const put  =async(path,Options)=>{
    const res= await request.put(path,Options);
    return res.data;
}
export const getpaging  =async(path,pageSizeInput,pageNumber,keyWord,departmentName,categoryName)=>{
    const res= await request.get(path+'?PageSize='+pageSizeInput+ '&PageNumber=' + pageNumber + 
    "&KeyWord=" + keyWord + "&DepartmentName=" + departmentName + "&FixedCategoryName=" + categoryName);
    return res.data;
}
